mapper.select:
select
count(1) total,ceil(count(1) /
${pBean.pageSize})
totalPage,${pBean.currPage}
currPage,${pBean.pageSize}
pageSize,#{hpuserid}
hpuserid,${pBean.currPage} *
${pBean.pageSize} max,(${pBean.currPage}-1)
* ${pBean.pageSize} min
from
homepage
where
hpuserid=#{hpuserid}

select * from homepage where
hpuserid=#{hpuserid}
order by hpdate desc limit #{min},#{max}
